export const getRegionKeyByLandmarkId = (landmarkId) => {
  for (const regionKey in landmarksByRegion) {
    if (Object.prototype.hasOwnProperty.call(landmarksByRegion, regionKey)) {
      const found = landmarksByRegion[regionKey].find(
        (landmark) => landmark.id === landmarkId
      );
      if (found) {
        return regionKey;
      }
    }
  }
  return null;
};
